import { useDispatch, useSelector } from "react-redux";
import {toast} from "react-hot-toast"
import {add , remove} from "../redux/Slices/cartSlice"

const Product = ({post}) => {

  const {cart} = useSelector((state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to Cart");
  }

  const removeFromCart = () => {
    dispatch(remove(post.id));
    toast.error("Item removed from Cart");
  }
  return (
    <div className="flex flex-col items-center justify-between 
          hover:scale-110 transition duration-300 ease-in gap-3 p-4 mt-10 ml-5 rounded-xl outline">
          <div>
            <p className=" truncate text-gray-700 font-semibold text-lg text-left w-40 mt-1 "> 
              {post.title} 
            </p>
          </div>
        <div>
          <p className="w-40 text-gray-400 font-normal text-[10px] text-left"> 
            {post.description.split(" ").slice(0,10).join(" ") + "..." }
          </p>
        </div>

        <div className="h-[180px]">
        <img src={post.image} alt="" className="h-full w-full " />
       </div>
       
      </div>
    
  )
};

export default Product;
