    <div>
      {
        cart.length > 0 ?
        (
          <div>
            {
              cart.map( (item , index) => {
                return <CartItem key = {item.id} item = {item} itemIndex = {index} />
              })
            }

            <div>
              <div>
                <div>Your Cart</div>
