                <p>
                  <span> Total item = {cart.length} </span>
                </p>
              </div>

              <div>
                <p>
                  Total Amount : ${totalAmount}
                </p>
              </div>
            </div>

          </div>

          
        ) :
        (
          <div>
            <h1>Cart Emtey</h1>
            <Link to={"/"} >
              <button>
                shop now
              </button>
            </Link>
          </div>
        )
      }
    </div>
  )
};

export default Cart;
